mapper.select:
SELECT T.* FROM(SELECT TO_CHAR(PRC_DT,'YYYY/MM/DD') AS PRC_DT, PRC_DT_CNT, PRC_CN, ERR_CN, CASE WHEN ERR_CN = 0 THEN '0.0%' WHEN PRC_CN = 0 THEN '0.0%' WHEN ROUND((ERR_CN/PRC_DT_CNT)*100,1) = 100 THEN '100%' ELSE CONCAT(FORMAT((ERR_CN/PRC_DT_CNT)*100,1),'%') END AS ERR_RAT, CASE WHEN PRC_CN = 0 THEN '0.0%' WHEN ROUND(((PRC_CN+ERR_CN)/PRC_DT_CNT)*100,1) = 100 THEN '100%' ELSE CONCAT(FORMAT(((PRC_CN+ERR_CN)/PRC_DT_CNT)*100,1),'%') END AS PRC_RAT, VERIFY_UPDATE_CN, VERIFY_CN, CASE WHEN VERIFY_UPDATE_CN =0 AND VERIFY_CN=0 THEN '0.0%' WHEN ROUND((VERIFY_UPDATE_CN/VERIFY_CN)*100,1) = 100 THEN '100%' ELSE CONCAT(FORMAT((VERIFY_UPDATE_CN/VERIFY_CN)*100,1),'%') END AS VERIFY_UPDATE_RAT, A_Y AS AY, A_N AS AN, B_Y AS "BY", B_N AS BN, C_Y AS CY, C_N AS CN, D_Y AS DY, D_N AS DN, E_Y AS EY, E_N AS EN, TM_RECV_Y, TM_RECV_N, SMS_RECV_Y, SMS_RECV_N, DM_RECV_Y, DM_RECV_N, EMAIL_RECV_Y, EMAIL_RECV_N, TM_OFFER_Y, TM_OFFER_N, DM_OFFER_Y, DM_OFFER_N, EMAIL_OFFER_Y, EMAIL_OFFER_N, SMS_OFFER_Y, SMS_OFFER_N FROM TB_EM_IMR_DAILY_STATICS WHERE MAJOR_CLASS ='TOTAL'
AND TO_CHAR(PRC_DT,'YYYYMM') = REPLACE(#{textPrcDt},'-','')
) T
ORDER BY T.${columnName} ${sortOrder}
ORDER BY T.PRC_DT
LIMIT #{pageSize} OFFSET #{startPageNumber}

SELECT COUNT(*) TOT_ROW_CNT from(SELECT TO_CHAR(PRC_DT,'YYYYMM') AS PRC_DT FROM TB_EM_IMR_DAILY_STATICS WHERE MAJOR_CLASS ='TOTAL'
AND TO_CHAR(PRC_DT,'YYYY') = REPLACE(#{textPrcDt},'-','')
) T GROUP BY T.PRC_DT

SELECT TT.* FROM(SELECT T.PRC_DT AS PRC_DT, SUM(T.PRC_DT_CNT) AS PRC_DT_CNT, SUM(T.PRC_CN) AS PRC_CN, SUM(T.ERR_CN) AS ERR_CN, CASE WHEN SUM(T.ERR_CN) = 0 THEN '0.0%' WHEN SUM(T.PRC_CN) = 0 THEN '0.0%' WHEN FORMAT((SUM(T.ERR_CN)/SUM(T.PRC_DT_CNT))*100,1) = 100 THEN '100%' ELSE CONCAT(FORMAT((SUM(T.ERR_CN)/SUM(T.PRC_DT_CNT))*100,1),'%') END AS ERR_RAT, CASE WHEN SUM(T.PRC_CN) = 0 THEN '0.0%' WHEN FORMAT(((SUM(T.PRC_CN)+SUM(T.ERR_CN))/SUM(T.PRC_DT_CNT))*100,1) =100 THEN '100%' ELSE CONCAT(FORMAT(((SUM(T.PRC_CN)+SUM(T.ERR_CN))/SUM(T.PRC_DT_CNT))*100,1),'%') END AS PRC_RAT, SUM(T.VERIFY_UPDATE_CN) AS VERIFY_UPDATE_CN, SUM(T.VERIFY_CN) AS VERIFY_CN, CASE WHEN SUM(T.VERIFY_UPDATE_CN) =0 AND SUM(T.VERIFY_CN)=0 THEN '0.0%' WHEN FORMAT((SUM(T.VERIFY_UPDATE_CN)/SUM(T.VERIFY_CN))*100,1) = 100 THEN '100%' ELSE CONCAT(FORMAT((SUM(T.VERIFY_UPDATE_CN)/SUM(T.VERIFY_CN))*100,1),'%') END AS VERIFY_UPDATE_RAT, SUM(T.A_Y) AS AY, SUM(T.A_N) AS AN, SUM(T.B_Y) AS "BY", SUM(T.B_N) AS BN, SUM(T.C_Y) AS CY, SUM(T.C_N) AS CN, SUM(T.D_Y) AS DY, SUM(T.D_N) AS DN, SUM(T.E_Y) AS EY, SUM(T.E_N) AS EN, SUM(T.TM_RECV_Y) AS TM_RECV_Y, SUM(T.TM_RECV_N) AS TM_RECV_N, SUM(T.SMS_RECV_Y) AS SMS_RECV_Y, SUM(T.SMS_RECV_N) AS SMS_RECV_N, SUM(T.DM_RECV_Y) AS DM_RECV_Y, SUM(T.DM_RECV_N) AS DM_RECV_N, SUM(T.EMAIL_RECV_Y) AS EMAIL_RECV_Y, SUM(T.EMAIL_RECV_N) AS EMAIL_RECV_N, SUM(T.TM_OFFER_Y) AS TM_OFFER_Y, SUM(T.TM_OFFER_N) AS TM_OFFER_N, SUM(T.DM_OFFER_Y) AS DM_OFFER_Y, SUM(T.DM_OFFER_N) AS DM_OFFER_N, SUM(T.EMAIL_OFFER_Y) AS EMAIL_OFFER_Y, SUM(T.EMAIL_OFFER_N) AS EMAIL_OFFER_N, SUM(T.SMS_OFFER_Y) AS SMS_OFFER_Y, SUM(T.SMS_OFFER_N) AS SMS_OFFER_N from(SELECT TO_CHAR(PRC_DT,'YYYY/MM') AS PRC_DT, PRC_DT_CNT, PRC_CN, ERR_CN, VERIFY_UPDATE_CN, VERIFY_CN, A_Y, A_N, B_Y, B_N, C_Y, C_N, D_Y, D_N, E_Y, E_N, TM_RECV_Y, TM_RECV_N, SMS_RECV_Y, SMS_RECV_N, DM_RECV_Y, DM_RECV_N, EMAIL_RECV_Y, EMAIL_RECV_N, TM_OFFER_Y, TM_OFFER_N, DM_OFFER_Y, DM_OFFER_N, EMAIL_OFFER_Y, EMAIL_OFFER_N, SMS_OFFER_Y, SMS_OFFER_N FROM TB_EM_IMR_DAILY_STATICS WHERE MAJOR_CLASS ='TOTAL'
AND TO_CHAR(PRC_DT,'YYYY') = REPLACE(#{textPrcDt},'-','')
) T GROUP BY T.PRC_DT )TT
ORDER BY TT.${columnName} ${sortOrder}
ORDER BY TT.PRC_DT
LIMIT #{pageSize} OFFSET #{startPageNumber}

SELECT T.* FROM(SELECT A.ID_NO, A.CHRR_ID, B.CHRR_NM, B.DEPTNM, A.ELEMENTID AS ELEMENT_ID , TO_CHAR(A.IPDT_TIME,'YYYY/MM/DD') AS PRC_DT, (SELECT CONTRACT_ID FROM TB_EM_IMR_MASK_OBJ WHERE ELEMENTID=A.ELEMENTID) AS CONTRACT_ID, A.CUST_ID , A.QUERY_REASON , TO_CHAR(A.IPDT_TIME,'HH24:MI') AS PRC_TM FROM TB_EM_IMR_QUERY_HISTORY A INNER JOIN TB_EM_PC_CHRR B ON A.CHRR_ID = B.CHRR_ID WHERE 1=1
AND A.CUST_ID = #{custId}
AND TO_CHAR(A.IPDT_TIME,'YYYYMMDD') >= REPLACE(#{startPrcDt},'-','')
AND TO_CHAR(A.IPDT_TIME,'YYYYMMDD') <= REPLACE(#{endPrcDt},'-','')
)T
ORDER BY T.${columnName} ${sortOrder}
LIMIT #{pageSize} OFFSET #{startPageNumber}

    SELECT 
		COUNT(*) TOT_ROW_CNT
	FROM TB_EM_PC_CHRR
	WHERE 1=1

	AND CHRR_ID = #{chrrId}


	AND CHRR_NM = #{chrrNm}


	AND DEPTNM = #{deptnm}


	AND UYN = #{useYn}

   SELECT
   	T.*
   FROM(SELECT 
    		ID_NO,
    		CHRR_ID,
    		COMPANY_ID,
    		CHRR_PWD,
    		CHRR_NM,
    		DEPTNM,
    		RG_REASON,
    		UYN,
    		TO_CHAR(RG_DTM,'YYYY/MM/DD') AS RG_DT,
    		RG_ENO  AS RG_ID,
    		(SELECT CHRR_NM FROM TB_EM_PC_CHRR WHERE UYN='Y' AND CHRR_ID= A.RG_ENO)   AS RG_NM,
    		TO_CHAR(RG_DTM,'HH24:MI')  AS RG_TM
    	FROM TB_EM_PC_CHRR A
    	WHERE 1=1

    	AND A.CHRR_ID = #{chrrId}


    	AND A.CHRR_NM = #{chrrNm}


    	AND A.DEPTNM = #{deptnm}


    	AND UYN = #{useYn}

    )T

	ORDER BY T.${columnName} ${sortOrder} 


	ORDER BY T.RG_DT 

 	LIMIT  #{pageSize}
	OFFSET #{startPageNumber}

mapper.update:
UPDATE TB_EM_PC_CHRR SET COMPANY_ID = #{companyId}, CHRR_NM = #{chrrNm}, DEPTNM = #{deptnm},
CHRR_PWD = #{chrrPwd,jdbcType=VARCHAR},
RG_REASON = #{rgReason}, CHG_DTM = SYSDATE() WHERE ID_NO = #{idNo}

mapper.select:
SELECT ELEMENTID AS ELEMENT_ID, IMG_FILE_NAME , IMG_FORMAT_TYPE , MASK_PRG_STSC, USER_CONFIRM, USER_UPDATE_YN, INTVISION_IMR, BPR_IMR, FST_IMR_PAGE, CUST_ID, CONTRACT_ID, PRC_DT, RESULT_IMG_PATH, IMG_PATH_ORG, CASE WHEN MASK_PRG_STSC ='10' THEN '미처리' WHEN MASK_PRG_STSC ='20' THEN '처리중' WHEN MASK_PRG_STSC ='80' THEN '처리(완료)' WHEN MASK_PRG_STSC ='90' THEN '탐지(완료)' WHEN MASK_PRG_STSC ='99' THEN '반영(완료)' WHEN MASK_PRG_STSC ='98' THEN '반영(실패)' WHEN MASK_PRG_STSC ='95' THEN '검증(완료)' ELSE '' END AS MASK_PRG_STSC_TXT, CASE WHEN USER_CONFIRM = '00' THEN '확인전' WHEN USER_CONFIRM = '99' THEN '확인완료' ELSE '확인전' END AS USER_CONFIRM_TXT,
CASE WHEN USER_UPDATE_YN = '01' THEN '수정' WHEN USER_UPDATE_YN = '02' THEN '유지' ELSE '확인전' END AS USER_UPDATE_YN_TXT FROM TB_EM_IMR_MASK_OBJ WHERE 1=1 AND MASK_PRG_STSC > 20 AND INTVISION_IMR IS NOT NULL
AND PRC_DT = REPLACE(#{textPrcDt},'-','')
AND CUST_ID = #{custId}
AND CONTRACT_ID = #{contractId}
ORDER BY ${columnName} ${sortOrder}
LIMIT #{pageSize} OFFSET #{startPageNumber}

SELECT COUNT(*) TOT_ROW_CNT FROM TB_EM_IMR_MASK_OBJ WHERE 1=1 AND MASK_PRG_STSC > 20 AND INTVISION_IMR IS NOT NULL
AND PRC_DT = REPLACE(#{textPrcDt},'-','')
AND CUST_ID = #{custId}
AND CONTRACT_ID = #{contractId}

mapper.update:
    	UPDATE TB_EM_IMR_MASK_OBJ
    	SET INTVISION_IMR = #{intvisionImr}
    	  , USER_CONFIRM  = #{userConfirm}
		  ,	USER_UPDATE_YN = #{userUpdateYn}
		  , CHG_DTM = SYSDATE()
		  , CHG_ENO = #{chgEno}
    	WHERE ELEMENTID = #{elementId}

    	UPDATE TB_EM_IMR_MASK_OBJ
    	SET MASK_PRG_STSC = '85'
            , RESULT_IMG_PATH = NULL
	  		, CHG_DTM = SYSDATE()
	  		, CHG_ENO = #{chgEno}
    	WHERE ELEMENTID = #{elementId}
