mapper.update:
    	UPDATE TB_EM_PC_CHRR
		SET 
			CHG_DTM  = SYSDATE, 
			CHG_ENO  = #{chrrId}, 
			CHRR_PWD = #{chrrPwd}
		WHERE ID_NO=#{idNo}

mapper.select:
SELECT * FROM ( SELECT TA.* ,ROWNUM RNUM FROM ( SELECT PRC_DT, CHRR_ID, ELEMENTID AS ELEMENT_ID , PURPOSE, QUERY_REASON FROM TB_EM_FPPN_QUERY_HISTORY WHERE 1=1
AND CHRR_ID = #{custId}
AND PRC_DT >= REPLACE(#{startPrcDt},'-','')
AND PRC_DT <= REPLACE(#{endPrcDt},'-','')
) TA WHERE ROWNUM < ((#{pageNumber} * #{pageSize}) + 1)
ORDER BY TA.${columnName} ${sortOrder}
) WHERE RNUM >= (((#{pageNumber} - 1) * #{pageSize}) + 1)

    SELECT 
		COUNT(*) TOT_ROW_CNT
	FROM TB_EM_PC_CHRR
	WHERE 1=1

	AND CHRR_ID = #{chrrId}


	AND CHRR_NM = #{chrrNm}


	AND DEPTNM = #{deptnm}


	AND UYN = #{useYn}

SELECT * FROM ( SELECT TA.* ,ROWNUM RNUM FROM ( SELECT ID_NO, CHRR_ID, COMPANY_ID, CHRR_PWD, CHRR_NM, DEPTNM, RG_REASON, UYN, TO_CHAR(RG_DTM,'YYYY/MM/DD') AS RG_DT, RG_ENO AS RG_ID, (SELECT CHRR_NM FROM TB_EM_PC_CHRR WHERE UYN='Y' AND CHRR_ID= A.RG_ENO) AS RG_NM, TO_CHAR(RG_DTM,'HH24:MI') AS RG_TM FROM TB_EM_PC_CHRR A WHERE 1=1
AND A.CHRR_ID = #{chrrId}
AND A.CHRR_NM = #{chrrNm}
AND A.DEPTNM = #{deptnm}
AND UYN = #{useYn}
ORDER BY RG_DTM ) TA WHERE ROWNUM < ((#{pageNumber} * #{pageSize}) + 1)
ORDER BY TA.${columnName} ${sortOrder}
ORDER BY TA.RG_DT
) WHERE RNUM >= (((#{pageNumber} - 1) * #{pageSize}) + 1)

mapper.update:
UPDATE TB_EM_PC_CHRR SET COMPANY_ID = #{companyId}, CHRR_NM = #{chrrNm}, DEPTNM = #{deptnm},
CHRR_PWD = #{chrrPwd,jdbcType=VARCHAR},
RG_REASON = #{rgReason}, CHG_DTM = SYSDATE WHERE ID_NO = #{idNo}

    	UPDATE TB_EM_IMR_MASK_OBJ
    	SET INTVISION_IMR = #{intvisionImr}
    	  , USER_CONFIRM  = #{userConfirm}
		  ,	USER_UPDATE_YN = #{userUpdateYn}
		  , CHG_DTM = SYSDATE
		  , CHG_ENO = #{chgEno}
    	WHERE ELEMENTID = #{elementId}

    	UPDATE TB_EM_IMR_MASK_OBJ
    	SET MASK_PRG_STSC = '85'
            , RESULT_IMG_PATH = NULL
	  		, CHG_DTM = SYSDATE
	  		, CHG_ENO = #{chgEno}
    	WHERE ELEMENTID = #{elementId}

mapper.select:
SELECT * FROM ( SELECT TA.* ,ROWNUM RNUM FROM (
SELECT T.PRC_DT, SUM(T.ALL_CN) AS ALL_CN , SUM(T.PRC_CN) AS PRC_CN, SUM(T.MASK_CN) AS MASK_CN, SUM(T.NOMASK_CN) AS NOMASK_CN, SUM(T.ERR_CN) AS ERR_CN, CASE WHEN SUM(T.PRC_CN) = 0 THEN '0.0%' WHEN (SUM(T.PRC_CN)/SUM(T.ALL_CN))*100 = 100 THEN '100%' ELSE CONCAT(TO_CHAR(ROUND((SUM(T.PRC_CN)/SUM(T.ALL_CN))*100,1) ,'FM90.0') ,'%') END PRC_RAT, CASE WHEN SUM(T.MASK_CN) = 0 THEN '0.0%' WHEN (SUM(T.MASK_CN)/SUM(T.PRC_CN))*100 = 100 THEN '100%' ELSE CONCAT(TO_CHAR(ROUND((SUM(T.MASK_CN)/SUM(T.PRC_CN))*100,1) ,'FM90.0') ,'%') END MASK_RAT FROM (SELECT TO_CHAR(TO_DATE(PRC_DT,'YYYY-MM-DD'),'YYYY-MM-DD') AS PRC_DT, PRC_DT_CNT AS ALL_CN, PRC_CN, MASK_CN, NOMASK_CN, ERR_CN FROM TB_EM_FPPN_DAILY_STATICS WHERE 1=1
AND PRC_DT >= REPLACE(#{startPrcDt},'-','')
AND PRC_DT <= REPLACE(#{endPrcDt},'-','')
)T GROUP BY T.PRC_DT ) TA WHERE ROWNUM < ((#{pageNumber} * #{pageSize}) + 1)
ORDER BY TA.${columnName} ${sortOrder}
ORDER BY TA.PRC_DT
) WHERE RNUM >= (((#{pageNumber} - 1) * #{pageSize}) + 1)

SELECT * FROM ( SELECT TA.* ,ROWNUM RNUM FROM ( SELECT T.PRC_DT, SUM(T.ALL_CN) ALL_CN, SUM(T.PRC_CN) PRC_CN, SUM(T.MASK_CN) MASK_CN, SUM(T.NOMASK_CN) NOMASK_CN, SUM(T.ERR_CN) ERR_CN, CASE WHEN SUM(T.PRC_CN) = 0 THEN '0.0%' WHEN (SUM(T.PRC_CN)/SUM(T.ALL_CN))*100 = 100 THEN '100%' ELSE CONCAT(TO_CHAR(ROUND((SUM(T.PRC_CN)/SUM(T.ALL_CN))*100,1) ,'FM90.0') ,'%') END PRC_RAT, CASE WHEN SUM(T.MASK_CN) = 0 THEN '0.0%' WHEN (SUM(T.MASK_CN)/SUM(T.PRC_CN))*100 = 100 THEN '100%' ELSE CONCAT(TO_CHAR(ROUND((SUM(T.MASK_CN)/SUM(T.PRC_CN))*100,1) ,'FM90.0') ,'%') END MASK_RAT FROM( SELECT TO_CHAR(TO_DATE(PRC_DT,'YYYYMMDD'),'YYYY-MM') AS PRC_DT, PRC_DT_CNT AS ALL_CN, PRC_CN, MASK_CN, NOMASK_CN, ERR_CN FROM TB_EM_FPPN_DAILY_STATICS WHERE 1=1
AND TO_CHAR(TO_DATE(PRC_DT,'YYYYMMDD'),'YYYYMM') >= REPLACE(#{startPrcDt},'-','')
AND TO_CHAR(TO_DATE(PRC_DT,'YYYYMMDD'),'YYYYMM') <= REPLACE(#{endPrcDt},'-','')
)T GROUP BY T.PRC_DT ) TA WHERE ROWNUM < ((#{pageNumber} * #{pageSize}) + 1)
ORDER BY TA.${columnName} ${sortOrder}
ORDER BY TA.PRC_DT
) WHERE RNUM >= (((#{pageNumber} - 1) * #{pageSize}) + 1)

SELECT *FROM (SELECT TA.* ,ROWNUM RNUM FROM (
) TA WHERE ROWNUM < ((#{pageNumber} * #{pageSize}) + 1) ) WHERE RNUM >= (((#{pageNumber} - 1) * #{pageSize}) + 1)
ORDER BY TTT.${columnName} ${sortOrder}
