mapper.update:
    	UPDATE TB_EM_PC_CHRR
		SET 
			CHG_DTM  = SYSDATE, 
			CHG_ENO  = #{chrrId}, 
			CHRR_PWD = #{chrrPwd}
		WHERE ID_NO=#{idNo}

mapper.select:
SELECT * FROM ( SELECT TA.* ,ROWNUM RNUM FROM ( SELECT A.ID_NO, A.CHRR_ID, B.CHRR_NM, A.QUERY_TYPE, A.PURPOSE, B.DEPTNM, A.ELEMENTID AS ELEMENT_ID , TO_CHAR(A.IPDT_TIME,'YYYY/MM/DD') AS PRC_DT, (SELECT CONTRACT_ID FROM TB_EM_IMR_MASK_OBJ WHERE ELEMENTID=A.ELEMENTID) AS CONTRACT_ID, A.CUST_ID , A.QUERY_REASON , TO_CHAR(A.IPDT_TIME,'HH24:MI') AS PRC_TM FROM TB_EM_FPPN_QUERY_HISTORY A INNER JOIN TB_EM_PC_CHRR B ON A.CHRR_ID = B.CHRR_ID WHERE 1=1
AND A.CUST_ID = #{custId}
AND TO_CHAR(A.IPDT_TIME,'YYYYMMDD') >= REPLACE(#{startPrcDt},'-','')
AND TO_CHAR(A.IPDT_TIME,'YYYYMMDD') <= REPLACE(#{endPrcDt},'-','')
) TA WHERE ROWNUM < ((#{pageNumber} * #{pageSize}) + 1)
ORDER BY TA.${columnName} ${sortOrder}
) WHERE RNUM >= (((#{pageNumber} - 1) * #{pageSize}) + 1)

    SELECT 
		COUNT(*) TOT_ROW_CNT
	FROM TB_EM_PC_CHRR
	WHERE 1=1

	AND CHRR_ID = #{chrrId}


	AND CHRR_NM = #{chrrNm}


	AND DEPTNM = #{deptnm}


	AND UYN = #{useYn}

SELECT * FROM ( SELECT TA.* ,ROWNUM RNUM FROM ( SELECT ID_NO, CHRR_ID, COMPANY_ID, CHRR_PWD, CHRR_NM, DEPTNM, RG_REASON, UYN, TO_CHAR(RG_DTM,'YYYY/MM/DD') AS RG_DT, RG_ENO AS RG_ID, (SELECT CHRR_NM FROM TB_EM_PC_CHRR WHERE UYN='Y' AND CHRR_ID= A.RG_ENO) AS RG_NM, TO_CHAR(RG_DTM,'HH24:MI') AS RG_TM FROM TB_EM_PC_CHRR A WHERE 1=1
AND A.CHRR_ID = #{chrrId}
AND A.CHRR_NM = #{chrrNm}
AND A.DEPTNM = #{deptnm}
AND UYN = #{useYn}
ORDER BY RG_DTM ) TA WHERE ROWNUM < ((#{pageNumber} * #{pageSize}) + 1)
ORDER BY TA.${columnName} ${sortOrder}
ORDER BY TA.RG_DT
) WHERE RNUM >= (((#{pageNumber} - 1) * #{pageSize}) + 1)

mapper.update:
UPDATE TB_EM_PC_CHRR SET COMPANY_ID = #{companyId}, CHRR_NM = #{chrrNm}, DEPTNM = #{deptnm},
CHRR_PWD = #{chrrPwd,jdbcType=VARCHAR},
RG_REASON = #{rgReason}, CHG_DTM = SYSDATE WHERE ID_NO = #{idNo}

    	UPDATE TB_EM_IMR_MASK_OBJ
    	SET INTVISION_IMR = #{intvisionImr}
    	  , USER_CONFIRM  = #{userConfirm}
		  ,	USER_UPDATE_YN = #{userUpdateYn}
		  , CHG_DTM = SYSDATE
		  , CHG_ENO = #{chgEno}
    	WHERE ELEMENTID = #{elementId}

    	UPDATE TB_EM_IMR_MASK_OBJ
    	SET MASK_PRG_STSC = '85'
            , RESULT_IMG_PATH = NULL
	  		, CHG_DTM = SYSDATE
	  		, CHG_ENO = #{chgEno}
    	WHERE ELEMENTID = #{elementId}

mapper.select:
SELECT *FROM (SELECT TA.* ,ROWNUM RNUM FROM ( SELECT (SELECT CNM FROM TB_EM_PC_CODE WHERE C_ID = TT.BPR_BSN_DSC) BPR_BSN_DSC, TT.ALL_CN, TT.PRC_CN, TT.MASK_CN, TT.NOMASK_CN, TT.ERR_CN, CASE WHEN TT.PRC_CN = 0 THEN '0.0%' WHEN (TT.PRC_CN/TT.ALL_CN)*100 = 100 THEN '100%' ELSE CONCAT(TO_CHAR(ROUND((TT.PRC_CN/TT.ALL_CN)*100,1),'FM90.0'),'%') END AS PRC_RAT ,CASE WHEN TT.MASK_CN = 0 THEN '0.0%' WHEN (TT.MASK_CN/TT.PRC_CN)*100 = 100 THEN '100%' ELSE CONCAT(TO_CHAR(ROUND((TT.MASK_CN/TT.PRC_CN)*100,1),'FM90.0'),'%') END AS MASK_RAT FROM(SELECT T.BPR_BSN_DSC ,SUM(T.ALL_CN) AS ALL_CN ,SUM(T.MASK_CN)+SUM(T.NOMASK_CN)+SUM(T.ERR_CN) AS PRC_CN ,SUM(T.MASK_CN) AS MASK_CN ,SUM(T.NOMASK_CN) AS NOMASK_CN ,SUM(T.ERR_CN) AS ERR_CN FROM(SELECT BPR_BSN_DSC ,1 AS ALL_CN ,CASE WHEN (MASK_PRG_STSC >=90 AND MASK_PRG_STSC<100) THEN 1 ELSE 0 END MASK_CN ,CASE WHEN MASK_PRG_STSC = 80 THEN 1 ELSE 0 END NOMASK_CN ,CASE WHEN MASK_PRG_STSC >100 THEN 1 ELSE 0 END ERR_CN FROM TB_EM_FPPN_MASK_OBJ WHERE 1=1 AND MASK_PRG_STSC > 20
AND PRC_DT >= REPLACE(#{startPrcDt},'-','')
AND PRC_DT <= REPLACE(#{endPrcDt},'-','')
) T WHERE 1=1 GROUP BY T.BPR_BSN_DSC )TT ) TA WHERE ROWNUM < ((#{pageNumber} * #{pageSize}) + 1) ) WHERE RNUM >= (((#{pageNumber} - 1) * #{pageSize}) + 1)
ORDER BY TTT.${columnName} ${sortOrder}

SELECT * FROM ( SELECT TA.* ,ROWNUM RNUM FROM ( SELECT TO_CHAR(TO_DATE(PRC_DT,'YYYY-MM-DD'),'YYYY-MM-DD') AS PRC_DT, PRC_DT_CNT AS ALL_CN, PRC_CN, MASK_CN, NOMASK_CN, ERR_CN, CASE WHEN PRC_CN = 0 THEN '0.0%' WHEN (PRC_CN/PRC_DT_CNT)*100 = 100 THEN '100%' ELSE CONCAT(TO_CHAR(ROUND((PRC_CN/PRC_DT_CNT)*100,1),'FM90.0'),'%') END PRC_RAT, CASE WHEN MASK_CN = 0 THEN '0.0%' WHEN (MASK_CN/PRC_CN)*100 = 100 THEN '100%' ELSE CONCAT(TO_CHAR(ROUND((MASK_CN /PRC_CN)*100,1),'FM90.0'),'%') END MASK_RAT FROM TB_EM_FPPN_DAILY_STATICS WHERE 1=1
AND PRC_DT >= REPLACE(#{startPrcDt},'-','')
AND PRC_DT <= REPLACE(#{endPrcDt},'-','')
) TA WHERE ROWNUM < ((#{pageNumber} * #{pageSize}) + 1)
ORDER BY TA.${columnName} ${sortOrder}
ORDER BY TA.PRC_DT
) WHERE RNUM >= (((#{pageNumber} - 1) * #{pageSize}) + 1)

SELECT * FROM ( SELECT TA.* ,ROWNUM RNUM FROM ( SELECT T.PRC_DT, SUM(T.ALL_CN) ALL_CN, SUM(T.PRC_CN) PRC_CN, SUM(T.MASK_CN) MASK_CN, SUM(T.NOMASK_CN) NOMASK_CN, SUM(T.ERR_CN) ERR_CN, CASE WHEN SUM(T.PRC_CN) = 0 THEN '0.0%' WHEN (SUM(T.PRC_CN)/SUM(T.ALL_CN))*100 = 100 THEN '100%' ELSE CONCAT(TO_CHAR(ROUND((SUM(T.PRC_CN)/SUM(T.ALL_CN))*100,1) ,'FM90.0') ,'%') END PRC_RAT, CASE WHEN SUM(T.MASK_CN) = 0 THEN '0.0%' WHEN (SUM(T.MASK_CN)/SUM(T.PRC_CN))*100 = 100 THEN '100%' ELSE CONCAT(TO_CHAR(ROUND((SUM(T.MASK_CN)/SUM(T.PRC_CN))*100,1) ,'FM90.0') ,'%') END MASK_RAT FROM( SELECT TO_CHAR(TO_DATE(PRC_DT,'YYYYMMDD'),'YYYYMM') AS PRC_DT, PRC_DT_CNT AS ALL_CN, PRC_CN, MASK_CN, NOMASK_CN, ERR_CN FROM TB_EM_FPPN_DAILY_STATICS WHERE 1=1
AND TO_CHAR(TO_DATE(PRC_DT,'YYYYMMDD'),'YYYYMM') >= REPLACE(#{startPrcDt},'-','')
AND TO_CHAR(TO_DATE(PRC_DT,'YYYYMMDD'),'YYYYMM') <= REPLACE(#{endPrcDt},'-','')
)T GROUP BY T.PRC_DT ) TA WHERE ROWNUM < ((#{pageNumber} * #{pageSize}) + 1)
ORDER BY TA.${columnName} ${sortOrder}
ORDER BY TA.PRC_DT
) WHERE RNUM >= (((#{pageNumber} - 1) * #{pageSize}) + 1)

SELECT *FROM (SELECT TA.* ,ROWNUM RNUM FROM (
) TA WHERE ROWNUM < ((#{pageNumber} * #{pageSize}) + 1) ) WHERE RNUM >= (((#{pageNumber} - 1) * #{pageSize}) + 1)
ORDER BY TTT.${columnName} ${sortOrder}
